package app.android.dmzj.request;

import javax.crypto.Cipher;

import java.io.ByteArrayOutputStream;
import java.security.KeyFactory;
import java.security.NoSuchAlgorithmException;
import java.security.PrivateKey;
import java.security.interfaces.RSAPrivateKey;
import java.security.spec.InvalidKeySpecException;
import java.security.spec.PKCS8EncodedKeySpec;
import java.util.Base64;

public class RSA_Decode {
    static String V4_PRIVATE_KEY = "";

    public byte[] decryptByPrivateKey(String content) throws Exception {
        byte[] inputByte = Base64.getDecoder().decode(content);
        //base64编码的私钥
        byte[] decoded = Base64.getDecoder().decode(V4_PRIVATE_KEY);
        RSAPrivateKey priKey = (RSAPrivateKey) KeyFactory.getInstance("RSA").generatePrivate(new PKCS8EncodedKeySpec(decoded));
        //RSA解密
        Cipher cipher = Cipher.getInstance("RSA/ECB/PKCS1Padding");
        cipher.init(Cipher.DECRYPT_MODE, priKey);
        int count = 0;
        int start = 0;
        int end = 0;
        byte[] out = new byte[0];
        while (start<inputByte.length){
            end = start+128;
            if(end>inputByte.length)
                end=inputByte.length;
            out = NetConnection.byteCat(out,cipher.doFinal(NetConnection.subBytes(inputByte,start,end)));
            count+=1;
            start = 128*count;
        }
        return out;
    }
}
